sysauto_script.script:
var record = new GlideRecord('x_1553854_inventor_daily_goods');
record.addQuery('u_boolean_1', '!=', true);
record.addQuery('priority', '!=', 5);
record.query();
while (record.next()) {
    var rightNow = new GlideDateTime();
    var time = new GlideDateTime(record.expirationdate);
    //var durationOfUse = gs.dateDiff(rightNow,record.purchasedate,true);
    var dur = GlideDateTime.subtract(rightNow, time);
    //gs.info("DueSoon NeedIt Task record = " + record.goodsname);
    dur.getDisplayValue();
    var du = dur.getRoundedDayPart();
    //gs.info(du.getDisplayValue());
    if (du < 30) {
        record.priority = 1;
    } else if (du < 90) {
        record.priority = 2;
    } else if (du < 180) {
        record.priority = 3;
    } else if (du > 180) {
        record.priority = 4;
    }
    record.update();
    //record.unitpriceperday=perday;
	gs.eventQueue('x_1553854_inventor_daily_goods');
    gs.info("set the priority to " + record.priority);

}
